import React, { Component } from 'react'
import axios from 'axios'
// Bootstrap
import Navbar from 'react-bootstrap/Navbar';
import { Brand, Toggle, Collapse } from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import { Item, Link } from 'react-bootstrap/Nav';

import logo from '../../logo.svg';
import './style.css';

class NavigationBar extends Component {
    logout = e => {
        e.preventDefault();
        console.log('logging out');
        axios
            .post('/user/logout').then(response => {
                console.log(response.data)
                if (response.status === 200) {
                    this.props.updateUser({
                        loggedIn: false,
                        username: null
                    })
                }
            })
            .catch(error => {
                console.log('Logout error')
            })
    }

    render() {
        const loggedIn = this.props.loggedIn;
        console.log('navbar render, props: ')
        console.log(this.props);

        return (
            <Navbar bg="dark" variant="dark" expand="lg">
                <Brand href="#home">Colorado Campgrounds</Brand>
                <img src={logo} className="logo" alt="logo" />
                <Toggle aria-controls="basic-navbar-nav" />
                <Collapse id="basic-navbar-nav">
                <Nav>
                    { loggedIn 
                        ? 
                        <Item>
                            <Link href="#" onClick={this.logout}>Logout</Link>
                        </Item>
                        :
                        <Item>
                            <Link href="/">Home</Link>
                            <Link href="/login">Login</Link>
                            <Link href="/signup">Signup</Link>
                        </Item>
                    }
                </Nav>
                </Collapse>
            </Navbar>
        );
    }
}

export default NavigationBar;
