import React, { Component } from 'react';
// React-Bootstrap
import Form from 'react-bootstrap/Form';
import { Row, Group, Label, Control } from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';

const weatherForm = document.querySelector('#weatherSearchForm');
const search = document.querySelector('#cityInput');
const message1 = document.querySelector('#message-1');
const message2 = document.querySelector('#message-2');

weatherForm.addEventListener('submit', e => {
  e.preventDefault();

  const location = search.value;
  message1.textContent = 'Loading';
  message2.textContent = '';

  fetch(`/weather?address=${location}`).then(response => {
    response.json().then(({ forecast, location, error }) => {
      if (error) {
        message1.textContent = error;
      } else {
        message1.textContent = location;
        message2.textContent = forecast;
      }
    });
  });
});

class SearchForm extends Component {
  state = {
    city: ''
  }



  render() {
    return (
      <Form id="weatherSearchForm">
        <Group as={Col} controlId="formGridAddress1">
          <Label>Address</Label>
          <Control placeholder="1234 Main St" />
        </Group>  
        <Group as={Col} controlId="formGridCity">
          <Label>City</Label>
          <Control id="cityInput" />
        </Group>
        <Row as={Col}>
          <Group as={Col} controlId="formGridState">
            <Label>State</Label>
            <Control as="select">
              <option>Choose...</option>
              <option>...</option>
            </Control>
          </Group>
          <Group as={Col} controlId="formGridZip">
            <Label>Zip</Label>
            <Control />
          </Group>
        </Row>    
        <Button as={Col} variant="primary" type="submit">
          Search
        </Button>      
      </Form>
    )
  }
}

export default SearchForm;
